mapper.insert:
INSERT INTO EMP
(
	  E_NO
	, E_NM
	, E_ID
	, E_PW
	, E_DEPT
	, E_POSI
	, E_IN_DT
	, E_OUT_dT
)
VALUES
(
	  TO_CHAR(SYSDATE,'YYYY') || LPAD(EMP_NUM_SEQ.NEXTVAL, 3, '0')
	, #{eNm}
	, #{eId}
	, #{ePw}
	, #{eDept}
	, #{ePosi}
	, #{eInDt}
	, #{eOutDt}
)

mapper.update:
UPDATE EMP
   SET E_NM = #{eNm}
	 , E_ID = NVL(#{eId}, E_NO)
	 , E_DEPT = #{eDept}
	 , E_POSI = #{ePosi}
	 , E_IN_DT = #{eInDt}
	 , E_OUT_dT = #{eOutDt}
 WHERE E_NO = #{eNo}
